mapper.update:
update  easybuy_user


        password = #{password}


where LoginName = #{loginName} and isDel = 0

mapper.select:
SELECT u.id, u.loginName, u.userName, u.password, sex, identityCode, email, mobile, a.phone, roleId, u.createTime, a.address, total_cost FROM easybuy_user u LEFT JOIN ( SELECT userId, MAX(address) AS address, MAX(phone) AS phone FROM easybuy_user_address
isDefault = #{isDefault}
GROUP BY userId LIMIT 1 ) a ON u.id = a.userId LEFT JOIN ( SELECT userId, SUM(cost) AS total_cost FROM easybuy_order GROUP BY userId ) o ON u.id = o.userId WHERE u.loginName = #{loginName} AND u.isDel = 0
